import Link from "next/link";
import blogListStyles from "../styles/components/bloglist.module.scss";
import matter from "gray-matter";
import { useState } from "react";

const BlogList = () => {
  const [posts, setPosts] = useState([]);
  const getPosts = async () => {
    const posts = await (ctx => {
      const keys = ctx.keys();
      const values = keys.map(ctx);
      const data = keys.map((key, index) => {
        // Create slug from filename
        const slug = key
          .replace(/^.*[\\\/]/, "")
          .split(".")
          .slice(0, -1)
          .join(".");
        const value = values[index];
        // Parse document
        const document = matter(value.default);
        return {
          document,
          slug
        };
      });
      return data;
    })(require.context("../content/posts", true, /\.md$/));
    return posts;
  };
  // function mapPosts(data) {
  //   data.posts.map(post => console.log(post));
  // }
  // getPosts().then(data => mapPosts(data));
  getPosts();
  return (
    <ul className={blogListStyles.list}>
      <Link href="/">
        <li className={blogListStyles.li}>
          <div className={blogListStyles.hero_image}>
            <img src="https://source.unsplash.com/random" />
          </div>
          <div className={blogListStyles.blog__info}>
            <h1>Blog Title</h1>
            <h3> July 07, 1992</h3>
            <p>Blog summary</p>
          </div>
        </li>
      </Link>
    </ul>
  );
};

